import { Component} from '@angular/core';
import { MeetingService } from '../meetservice/meeting.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-meeting-detailes',
  templateUrl: './meeting-detailes.component.html',
  styleUrls: ['./meeting-detailes.component.css']
})
export class MeetingDetailesComponent{

  meetings: any[] = []

  constructor(private meetingservice: MeetingService, private router: Router){
      this.meetings = this.meetingservice.getMeetings()
  }

  addMore(){
    this.router.navigateByUrl('/meet')

  }
  logout(){
    this.router.navigateByUrl('/login')
  }


}
<div class="container">

    <h1>Meetings details</h1>
    <div class="addbutton">
        <button (click)="addMore()" class="btn btn-primary addMore">Add More</button>
    </div>

<table class="main table  table-hover" style="border: 2px solid black;">

    <thead>
        <tr class="heading">
            <th>Meeting</th>
            <th>People</th>
            <th>Date</th>
        </tr>
    </thead>
    <tbody>
        <tr  *ngFor="let meeting of meetings">
            <td>{{meeting.meeting}}</td>
            <td>{{meeting.people}}</td>
            <td>{{meeting.date}}</td>
        </tr>
    </tbody>
</table>


    <button (click)="logout()" class="btn btn-primary logout">Logout</button>


</div>
