import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {



  data: any;
  loginsubmite: boolean = false;

  constructor(private router: Router){

  };

  sigin(data:object): void {
    this.loginsubmite = true;
    if (this.login.valid) {
      // Retrieve user data from local storage
      const storedUserData = localStorage.getItem('userData');
      if (storedUserData) {
        const userData = JSON.parse(storedUserData);
        if (
          userData.name === this.name.value &&
          userData.pwd === this.password.value
        ) {
          alert('Login successfully');
          this.router.navigateByUrl('/meet');
        } else {
          alert('Incorrect username or password');
        }
      } else {
        alert('User not found. Please register first.');
      }
    } else {
      alert('Please enter the credentials.');
    }
  }

  login = new FormGroup({
    name: new FormControl(''),
    password: new FormControl('')

  })



  get name(): FormControl
{
  return this.login.get("name") as FormControl;
}


get password(): FormControl
{
  return this.login.get("password") as FormControl
}
}
<form class="login" [formGroup]="login" (ngSubmit)="sigin(login.value)">

    <div class="main">
        <h1>Welcome to login session</h1>
        <input type="text" placeholder="Username" name="name" formControlName="name">
        <div class="form-text text-danger" *ngIf="name.errors?.['required'] && (name.touched|| loginsubmite)">
            Please enter username
        </div>
        <br>
        <input type="password" placeholder="Password" name="password" formControlName="password">
        <div class="form-text text-danger" *ngIf="password.errors?.['required'] && (password.touched || loginsubmite)">
            Please enter password
        </div>
        <br>
        <button class="btn btn-primary">Login</button>
        <button class="btn btn-primary Signup">Signup</button>
    </div>



</form>
