import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-meet',
  templateUrl: './meet.component.html',
  styleUrls: ['./meet.component.css']
})
export class MeetComponent implements OnInit {

  formsubmitted: boolean = false;

  constructor(private route: Router) {}

  ngOnInit() {



    }


  submitClient() {
    this.formsubmitted = true;
    if(this.login.valid){


      alert("Congrates meeting has been scheduled successfully")
      this.route.navigateByUrl('/meet-table')
      this.login.reset();


    }
    else{
      alert("Enter all the details")
    }



  }




  login = new FormGroup({
     meeting : new FormControl('', Validators.required),
     people : new FormControl('', Validators.required),
     date: new FormControl('', Validators.required  )
  })

  get meeting(): FormControl
{
  return this.login.get("meeting") as FormControl
}

get people (): FormControl
{
  return this.login.get("people") as FormControl
}

get date (): FormControl
{
  return this.login.get("date") as FormControl
}

}
